import React, { Component } from "react";
import { connect } from "react-redux";
import _ from "lodash";

import { search, categorize, filterRating, createMapData } from "../../utils";
import { MoviesTable, Pagination } from "../../components";
import { Input, Loading, ListGroup } from "../../components/common";

import { getMovies } from "../../actions/moviesAction";
import { getGenres } from "../../actions/genreAction";

import { Chart } from "react-google-charts";

import { GET_ALL_COUNTRIES_DATA } from "../../actions/stocksAction";

class Stocks extends Component {
  state = {
    genres: [],
    pageSize: 12,
    currentPage: 1,
    currentGenre: "All",
    searchFilter: "",
    rating: 0,
    countries: [],
  };

  componentDidMount() {
    this.props.getMovies();
    this.props.getGenres();
    this.props.GET_ALL_COUNTRIES_DATA();
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value, currentPage: 1 });
  };

  onPageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { currentGenre, currentPage, searchFilter, pageSize, rating } =
      this.state;

    const { movies, genres, loggedIn, countries } = this.props;

    let filterData = [];

    if (_.isEmpty(countries)) {
      return (
        <div className="background-container pt-5">
          <Loading />
        </div>
      );
    } else {
      filterData = createMapData(countries);
    }

    console.log(filterData);

    return (
      <Chart
        chartEvents={[
          {
            eventName: "select",
            callback: ({ chartWrapper }) => {
              const chart = chartWrapper.getChart();
              const selection = chart.getSelection();
              if (selection.length === 0) return;
              const region = filterData[selection[0].row + 1];
              console.log("Selected : " + region);
            },
          },
        ]}
        chartType="GeoChart"
        width="100%"
        height="400px"
        data={filterData}
      />
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    movies: state.movie.movies,
    genres: state.genre.genres,
    countries: state.stock.countries,
    loggedIn: state.auth.loggedIn,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getMovies: () => dispatch(getMovies()),
    getGenres: () => dispatch(getGenres()),
    GET_ALL_COUNTRIES_DATA: () => dispatch(GET_ALL_COUNTRIES_DATA()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Stocks);
